let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));

openEditResidentDialog();

function openEditResidentDialog() {
    customDialog.customDialog(htmlTemplate, EditResidentDialogController).subscribe();
}

function EditResidentDialogController(instance) {
    let vm = instance;

    vm.customerId = null;

    const phoneNumberPattern = /^\+[1-9]\d{1,14}$/;

    vm.entityName = entityName;
    vm.entityId = entityId;

    vm.widgetContext = widgetContext;
    vm.resident = null;
    vm.zone = null;
    vm.room = null;
    vm.wristband = null;

    vm.editResidentFormGroup = vm.fb.group({
      email: ['', [vm.validators.required, vm.validators.email]],
      firstName: ['', [vm.validators.required]],
      lastName: ['', [vm.validators.required]],
      gender: [null],
      phoneNumber: [null, [vm.validators.pattern(phoneNumberPattern)]],
      birthdate: [null],
      photo: [null, [vm.validators.required]],
      emergencyFirstname: [''],
      emergencyLastname: [''],
      emergencyRole: [''],
      emergencyPhoneNumber: [null, [vm.validators.pattern(phoneNumberPattern)]],
      chronicDiseases: [''],
      allergy: [''],
      zone: [null, [vm.validators.required]],
      room: [null, [vm.validators.required]],
      wristband: [null, [vm.validators.required]]
    });

    vm.genders = ['Male', 'Female'];

    vm.zones$ = getCustomerId().pipe(
        widgetContext.rxjs.tap((customerId) => {
            vm.customerId = customerId;
        }),
        widgetContext.rxjs.switchMap((ownerId) => {
            var query = {
                parameters: {
                    rootId: ownerId.id,
                    rootType: ownerId.entityType,
                    direction: 'FROM',
                    maxLevel: 1
                },
                relationType: 'Contains',
                assetTypes: ['AL_Zone']
            };
            return assetService.findByQuery(query).pipe(
                widgetContext.rxjs.map((zones) => {
                    return zones.sort((z1, z2) => z1.name.localeCompare(z2.name));
                })
            );
        }),
        widgetContext.rxjs.share()
    );

    vm.rooms$ = vm.editResidentFormGroup.get('zone').valueChanges.pipe(
        widgetContext.rxjs.tap(() => {
            vm.editResidentFormGroup.get('room').patchValue(null);
        }),
        widgetContext.rxjs.switchMap((zone) => {
            if (zone) {
                var query = {
                    parameters: {
                        rootId: zone.id.id,
                        rootType: zone.id.entityType,
                        direction: 'FROM',
                        maxLevel: 1
                    },
                    relationType: 'Contains',
                    assetTypes: ['AL_Room']
                };
                return assetService.findByQuery(query).pipe(
                    widgetContext.rxjs.map((rooms) => {
                        return rooms.sort((r1, r2) => r1.name.localeCompare(r2.name));
                    })
                );
            } else {
                return widgetContext.rxjs.of([]);
            }
        }),
        widgetContext.rxjs.share()
    );

    vm.wristbands$ = getCustomerId().pipe(
        widgetContext.rxjs.switchMap((ownerId) => {
           return getAvailableWristbands(ownerId);
        }),
        widgetContext.rxjs.share()
    );

    vm.entityComparisonFunction = function( option, value ) {
        if (!option || !value) {
            return option === value;
        } else {
            return option.id.id === value.id.id;
        }
    }

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editResidentFormGroup.markAsPristine();
        widgetContext.rxjs.forkJoin([
            saveResident(),
            saveRelations(entityId),
            saveAttributes(entityId)
        ]).subscribe(
            function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    loadResident();

    function saveResident() {
        const formValues = vm.editResidentFormGroup.value;
        vm.resident.email = formValues.email;
        vm.resident.firstName = formValues.firstName;
        vm.resident.lastName = formValues.lastName;
        return userService.saveUser(vm.resident);
    }

    function saveRelations(entityId) {
        let tasks = [];
        let room = vm.editResidentFormGroup.get('room').value;
        if (vm.room.id.id !== room.id.id) {
            let newRoomRelation = {
                type: 'Contains',
                typeGroup: 'COMMON',
                from: room.id,
                to: entityId
            };
            tasks.push([
                    entityRelationService.deleteRelation(vm.room.id, 'Contains', entityId),
                    entityRelationService.saveRelation(newRoomRelation)
            ]);
        }
        let wristband = vm.editResidentFormGroup.get('wristband').value;
        if (vm.wristband === null || vm.wristband.id.id !== wristband.id.id) {
            if (vm.wristband !== null) {
                tasks.push(unassignWristband(entityId, vm.wristband.id));
            }
            tasks.push(assignWristband(entityId, wristband.id));
        }
        if (tasks.length) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of(null);
        }
    }

    function saveAttributes(entityId) {
        let photo = vm.editResidentFormGroup.get('photo').value;
        let zone = vm.editResidentFormGroup.get('zone').value;
        let room = vm.editResidentFormGroup.get('room').value;
        let gender = vm.editResidentFormGroup.get('gender').value;
        let phoneNumber = vm.editResidentFormGroup.get('phoneNumber').value;
        let birthdate = vm.editResidentFormGroup.get('birthdate').value;
        if (birthdate) {
            birthdate = birthdate.getTime();
        } else {
            birthdate = null;
        }
        let emergencyFirstname = vm.editResidentFormGroup.get('emergencyFirstname').value;
        let emergencyLastname = vm.editResidentFormGroup.get('emergencyLastname').value;
        let emergencyRole = vm.editResidentFormGroup.get('emergencyRole').value;
        let emergencyPhoneNumber = vm.editResidentFormGroup.get('emergencyPhoneNumber').value;
        let chronicDiseases = vm.editResidentFormGroup.get('chronicDiseases').value;
        let allergy = vm.editResidentFormGroup.get('allergy').value;

        let attributesArray = [
            {key: 'photo', value: photo},
            {key: 'zone', value: zone.name},
            {key: 'room', value: room.name},
            {key: 'gender', value: gender},
            {key: 'phoneNumber', value: phoneNumber},
            {key: 'birthdate', value: birthdate},
            {key: 'emergencyFirstname', value: emergencyFirstname},
            {key: 'emergencyLastname', value: emergencyLastname},
            {key: 'emergencyRole', value: emergencyRole},
            {key: 'emergencyPhoneNumber', value: emergencyPhoneNumber},
            {key: 'chronicDiseases', value: chronicDiseases},
            {key: 'allergy', value: allergy}
        ];
        return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
    }

    function unassignWristband(entityId, wristbandId) {
        return deviceService.getDevice(wristbandId.id).pipe(
            widgetContext.rxjs.switchMap((wristband) => {
                return getAssignedUnassignedDeviceGroupIds().pipe(
                    widgetContext.rxjs.switchMap((groupIds) => {
                        wristband.label = '';
                        return widgetContext.rxjs.forkJoin([
                            deviceService.saveDevice(wristband),
                            entityRelationService.deleteRelation(entityId, 'Use', wristbandId),
                            entityGroupService.removeEntityFromEntityGroup(groupIds.assignedGroupId, wristbandId.id),
                            entityGroupService.addEntityToEntityGroup(groupIds.unassignedGroupId, wristbandId.id)
                        ]);
                    })
                );
            })
        );
    }

    function assignWristband(entityId, wristbandId) {
        let newWristbandRelation = {
            type: 'Use',
            typeGroup: 'COMMON',
            from: entityId,
            to: wristbandId
        };
        return deviceService.getDevice(wristbandId.id).pipe(
            widgetContext.rxjs.switchMap((wristband) => {
                return getAssignedUnassignedDeviceGroupIds().pipe(
                    widgetContext.rxjs.switchMap((groupIds) => {
                        wristband.label = vm.resident.firstName + ' ' + vm.resident.lastName;
                        return widgetContext.rxjs.forkJoin([
                            deviceService.saveDevice(wristband),
                            entityRelationService.saveRelation(newWristbandRelation),
                            entityGroupService.removeEntityFromEntityGroup(groupIds.unassignedGroupId, wristbandId.id),
                            entityGroupService.addEntityToEntityGroup(groupIds.assignedGroupId, wristbandId.id)
                        ]);
                    })
                );
            })
        );
    }

    function getAssignedUnassignedDeviceGroupIds() {
        return widgetContext.rxjs.forkJoin([
            getOrCreateEntityGroup(vm.customerId, 'DEVICE', 'Unassigned Devices'),
            getOrCreateEntityGroup(vm.customerId, 'DEVICE', 'Assigned Devices')
        ]).pipe(
            widgetContext.rxjs.map((groups) => {
              return { unassignedGroupId: groups[0].id.id, assignedGroupId: groups[1].id.id };
            })
        );
    }

    function loadResident() {
        widgetContext.rxjs.forkJoin([
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', 
                [
                    'photo',
                    'gender',
                    'phoneNumber',
                    'birthdate',
                    'emergencyFirstname',
                    'emergencyLastname',
                    'emergencyRole',
                    'emergencyPhoneNumber',
                    'chronicDiseases',
                    'allergy'
                ]
            ),
            loadResidentLocation(),
            loadResidentWristband(),
            userService.getUser(entityId.id)
        ]).subscribe(
            function (data) {
                var attributes = data[0];

                vm.editResidentFormGroup.get('photo').patchValue(getAttributeValue(attributes, 'photo', null), {emitEvent: false});
                vm.editResidentFormGroup.get('gender').patchValue(getAttributeValue(attributes, 'gender', null), {emitEvent: false});
                vm.editResidentFormGroup.get('phoneNumber').patchValue(getAttributeValue(attributes, 'phoneNumber', null), {emitEvent: false});
                let birthdate = getAttributeValue(attributes, 'birthdate', null);
                if (birthdate) {
                    birthdate = new Date(birthdate);
                }
                vm.editResidentFormGroup.get('birthdate').patchValue(birthdate, {emitEvent: false});
                vm.editResidentFormGroup.get('emergencyFirstname').patchValue(getAttributeValue(attributes, 'emergencyFirstname', ''), {emitEvent: false});
                vm.editResidentFormGroup.get('emergencyLastname').patchValue(getAttributeValue(attributes, 'emergencyLastname', ''), {emitEvent: false});
                vm.editResidentFormGroup.get('emergencyRole').patchValue(getAttributeValue(attributes, 'emergencyRole', ''), {emitEvent: false});
                vm.editResidentFormGroup.get('emergencyPhoneNumber').patchValue(getAttributeValue(attributes, 'emergencyPhoneNumber', null), {emitEvent: false});
                vm.editResidentFormGroup.get('chronicDiseases').patchValue(getAttributeValue(attributes, 'chronicDiseases', ''), {emitEvent: false});
                vm.editResidentFormGroup.get('allergy').patchValue(getAttributeValue(attributes, 'allergy', ''), {emitEvent: false});

                vm.zone = data[1][0];
                vm.room = data[1][1];
                vm.editResidentFormGroup.get('zone').patchValue(vm.zone);
                vm.editResidentFormGroup.get('room').patchValue(vm.room, {emitEvent: false});

                vm.wristband = data[2];
                vm.editResidentFormGroup.get('wristband').patchValue(vm.wristband, {emitEvent: false});

                vm.resident = data[3];
                vm.editResidentFormGroup.get('email').patchValue(vm.resident.email, {emitEvent: false});
                vm.editResidentFormGroup.get('firstName').patchValue(vm.resident.firstName, {emitEvent: false});
                vm.editResidentFormGroup.get('lastName').patchValue(vm.resident.lastName, {emitEvent: false});
            }
        );
    }

    function getAttributeValue(attributes, key, defaultValue) {
        let value = null;
        let found = attributes.filter((attribute) => attribute.key === key);
        if (found.length) {
            value = found[0].value;
        }
        if (value === null || value === '') {
            value = defaultValue;
        }
        return value;
    }

    function loadResidentLocation() {
        var roomQuery = {
            parameters: {
                rootId: entityId.id,
                rootType: entityId.entityType,
                direction: 'TO',
                maxLevel: 1
            },
            relationType: 'Contains',
            assetTypes: ['AL_Room']
        };
        return assetService.findByQuery(roomQuery).pipe(
            widgetContext.rxjs.switchMap((rooms) => {
                let room = rooms[0];
                var zoneQuery = {
                    parameters: {
                        rootId: room.id.id,
                        rootType: room.id.entityType,
                        direction: 'TO',
                        maxLevel: 1
                    },
                    relationType: 'Contains',
                    assetTypes: ['AL_Zone']
                };
                return assetService.findByQuery(zoneQuery).pipe(
                    widgetContext.rxjs.switchMap((zones) => {
                        let zone = zones[0];
                        return widgetContext.rxjs.of([zone, room]);
                    })
                );
            })
        );
    }

    function loadResidentWristband() {
        var wristbandQuery = {
            parameters: {
                rootId: entityId.id,
                rootType: entityId.entityType,
                direction: 'FROM',
                maxLevel: 1
            },
            relationType: 'Use',
            deviceTypes: ['AL_Wristband']
        };
        return deviceService.findByQuery(wristbandQuery).pipe(
            widgetContext.rxjs.map((wristbands) => {
                if (wristbands.length) {
                    let wristband = wristbands[0];
                    return {id: wristband.id, name: wristband.name };
                } else {
                    return null;
                }
            })
        );
    }

    function getAvailableWristbands(ownerId) {
        const query = {
            pageLink: {
                pageSize: 100,
                page: 0
            },
            entityFields: [
                {
                    type: 'ENTITY_FIELD',
                    key: 'name'
                }
            ],
            entityFilter: {
                type: 'entitiesByGroupName',
                groupType: 'DEVICE',
                ownerId: ownerId,
                entityGroupNameFilter: 'Unassigned Devices'
            },
            keyFilters: [
                {
                    key: {
                        type: 'ENTITY_FIELD',
                        key: 'type'
                    },
                    valueType: 'STRING',
                    predicate: {
                        type: 'STRING',
                        operation: 'EQUAL',
                        value: { defaultValue: 'AL_Wristband'},
                        ignoreCase: false
                    }
                }
            ]
        };
        return entityService.findEntityDataByQuery(query).pipe(
            widgetContext.rxjs.switchMap((pageData) => {
                const wristbands = convertEntityPageDataToNameIdPairs(pageData);
                return loadResidentWristband().pipe(
                    widgetContext.rxjs.map((wristband) => {
                        if (wristband != null) {
                            wristbands.push(wristband);
                        }
                        wristbands.sort((w1, w2) => w1.name.localeCompare(w2.name));
                        return wristbands;
                    })
                );
            })
        );
    }

    function convertEntityPageDataToNameIdPairs(pageData) {
        return pageData.data.map((entityData) => {
            return convertEntityDataToNameIdPair(entityData);
        });
    }

    function convertEntityDataToNameIdPair(entityData) {
        return {
            id: entityData.entityId,
            name: entityData.latest['ENTITY_FIELD']['name'].value
        };
    }

    function getOrCreateEntityGroup(ownerId, type, name) {
      return getEntityGroupByName(ownerId, name, type).pipe(
          widgetContext.rxjs.switchMap((group) => {
              if (group) {
                  return widgetContext.rxjs.of(group);
              } else {
                  var newGroup = {
                    type: type,
                    name: name,
                    ownerId: ownerId
                  };
                  return entityGroupService.saveEntityGroup(newGroup);
              }
          })
      );
    }

    function getEntityGroupByName(ownerId, groupName, groupType) {
      var entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
      return entityGroupService.getEntityGroupsByOwnerIdAndPageLink(ownerId.entityType, ownerId.id, groupType, entityGroupsPageLink, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((group) => group.name === groupName);
            } else {
                return null;
            }
          })
      );
    }


    function getCustomerId() {
        const customerAliasId = widgetContext.aliasController.getEntityAliasId('Customer');
        return widgetContext.aliasController.getAliasInfo(customerAliasId).pipe(
            widgetContext.rxjs.map((info) => {
              return {id: info.currentEntity.id, entityType: info.currentEntity.entityType};  
            })
        );
    }

}

