        font-size: 12px;
        font-weight: normal;
        color: #FFF;
        margin: 5px 0 0;
        padding: 0;
        line-height: 1;
        margin-left: 5px;
    }

    .humidity-switch-container {
        position: absolute;
        top: 20px;
        right: 20px;
    }

    .humidity-switch {
        position: relative;
        display: inline-block;
        width: 40px;
        height: 20px;
    }

    .humidity-switch input {
        opacity: 0;
        width: 0;
        height: 0;
    }

    .humidity-slider {
        position: absolute;
        cursor: pointer;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background-color: #838FB5;
        transition: .4s;
        border-radius: 20px;
    }

    .humidity-slider:before {
        position: absolute;
        content: "";
        height: 14px;
        width: 14px;
        left: 3px;
        bottom: 3px;
        background-color: white;
        transition: .4s;
        border-radius: 50%;
    }

    input:checked + .humidity-slider {
        background-color: #4287f5;
    }

    input:checked + .humidity-slider:before {
        transform: translateX(20px);
    }

