   ) {
    const arrow = document.querySelector('#tb-rotation-arrow');
    const panelTextLabel = document.getElementsByClassName('panel-text-label')[0];
    const panelButtons = document.getElementsByClassName('panel-buttons')[0];

    widgetContext.panelOpenState = !widgetContext.panelOpenState;
    arrow.classList.toggle('clicked');
    panelTextLabel.textContent = widgetContext.enabledFiltersString;

    if (widgetContext.panelOpenState) {
        panelTextLabel.style = "display: block";
        panelButtons.style = "display: none";
    } else {
        panelTextLabel.style = "display: none";
        panelButtons.style = "display: block";
    }
}

